import React, { useState, useRef, useEffect, useMemo } from 'react';
import { 
  Camera, 
  Download, 
  Trash2, 
  LayoutGrid, 
  Settings, 
  Upload, 
  RefreshCcw, 
  Zap,
  Calendar,
  User,
  CheckCircle2,
  Lock,
  ArrowLeftRight
} from 'lucide-react';

// ==========================================
// 定义类型接口
// ==========================================
interface GridImage {
  id: string;
  url: string | null;
}

interface GridConfig {
  gap: number;
  rounded: number;
  labName: string;
  studentName: string;
  showLogo: boolean;
  padding: number;
  gridCols: number;
  gridRows: number;
}

// ==========================================
// 【管理员配置区】
// 粘贴你的 Base64 字符串到下方的引号中即可锁死 Logo
// ==========================================
const LOCKED_LOGO_BASE64: string | null = 'data:image/png;base64,'; 

const STORAGE_KEY_CONFIG = 'hicode_grid_config';
const STORAGE_KEY_LOGO = 'hicode_grid_logo';

const App: React.FC = () => {
  const [config, setConfig] = useState<GridConfig>({
    gap: 10,
    rounded: 8,
    labName: '嗨创码AI实验室',
    studentName: '嗨创码',
    showLogo: true,
    padding: 40,
    gridCols: 5,
    gridRows: 5
  });

  const [logo, setLogo] = useState<string | null>(LOCKED_LOGO_BASE64);
  const [selectedDate, setSelectedDate] = useState<string>('');
  const [isExporting, setIsExporting] = useState(false);
  const [saveStatus, setSaveStatus] = useState(false);
  const [swapSourceIdx, setSwapSourceIdx] = useState<number | null>(null);

  useEffect(() => {
    const savedConfig = localStorage.getItem(STORAGE_KEY_CONFIG);
    if (savedConfig) {
      try {
        const parsed = JSON.parse(savedConfig);
        setConfig(prev => ({ ...prev, ...parsed }));
      } catch (e) {
        console.error("Failed to parse saved config", e);
      }
    }

    if (!LOCKED_LOGO_BASE64) {
      const savedLogo = localStorage.getItem(STORAGE_KEY_LOGO);
      if (savedLogo) setLogo(savedLogo);
    } else {
      setLogo(LOCKED_LOGO_BASE64);
    }

    const now = new Date();
    const y = now.getFullYear();
    const m = String(now.getMonth() + 1).padStart(2, '0');
    const d = String(now.getDate()).padStart(2, '0');
    setSelectedDate(`${y}-${m}-${d}`);
  }, []);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY_CONFIG, JSON.stringify(config));
    setSaveStatus(true);
    const timer = setTimeout(() => setSaveStatus(false), 2000);
    return () => clearTimeout(timer);
  }, [config]);

  useEffect(() => {
    if (logo && !LOCKED_LOGO_BASE64) {
      localStorage.setItem(STORAGE_KEY_LOGO, logo);
    }
  }, [logo]);

  const gridSize = config.gridCols * config.gridRows;
  const [images, setImages] = useState<GridImage[]>(
    Array.from({ length: 36 }, (_, i) => ({ id: `img-${i}`, url: null }))
  );
  
  const slogan = "让孩子成为AI世界的创造者";
  const watermarkText = "HiCode AI";
  
  const fileInputRef = useRef<HTMLInputElement>(null);
  const logoInputRef = useRef<HTMLInputElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const currentImages = useMemo(() => images.slice(0, gridSize), [images, gridSize]);

  const getFormattedDate = () => selectedDate.replace(/-/g, '.');

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []) as File[];
    if (files.length === 0) return;

    const newImages = [...images];
    let fileIdx = 0;

    for (let i = 0; i < gridSize && fileIdx < files.length; i++) {
      if (!newImages[i].url) {
        const file = files[fileIdx++];
        const reader = new FileReader();
        const currentIdx = i;
        reader.onload = (event) => {
          const result = event.target?.result as string;
          setImages(prev => {
            const updated = [...prev];
            updated[currentIdx] = { ...updated[currentIdx], url: result };
            return updated;
          });
        };
        reader.readAsDataURL(file);
      }
    }
    e.target.value = '';
  };

  const handleLogoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (LOCKED_LOGO_BASE64) return; 
    const file = e.target.files?.[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (event) => {
      setLogo(event.target?.result as string);
    };
    reader.readAsDataURL(file);
  };

  const clearImages = () => {
    if(window.confirm("确定要清空当前所有照片吗？")) {
      setImages(Array.from({ length: 36 }, (_, i) => ({ id: `img-${i}`, url: null })));
      setSwapSourceIdx(null);
    }
  };

  const removeImage = (index: number, e?: React.MouseEvent) => {
    if (e) e.stopPropagation();
    const updated = [...images];
    updated[index] = { ...updated[index], url: null };
    setImages(updated);
    if (swapSourceIdx === index) setSwapSourceIdx(null);
  };

  const handleGridClick = (index: number) => {
    if (swapSourceIdx === null) {
      if (!images[index].url) {
        fileInputRef.current?.click();
      } else {
        setSwapSourceIdx(index);
      }
    } else {
      if (swapSourceIdx === index) {
        setSwapSourceIdx(null);
        return;
      }
      const updated = [...images];
      const temp = updated[swapSourceIdx].url;
      updated[swapSourceIdx].url = updated[index].url;
      updated[index].url = temp;
      setImages(updated);
      setSwapSourceIdx(null);
    }
  };

  const switchLayout = (cols: number, rows: number) => {
    setConfig(prev => ({ ...prev, gridCols: cols, gridRows: rows }));
    setSwapSourceIdx(null);
  };

  const exportGrid = async () => {
    setIsExporting(true);
    const canvas = canvasRef.current;
    if (!canvas) { setIsExporting(false); return; }
    const ctx = canvas.getContext('2d');
    if (!ctx) { setIsExporting(false); return; }

    const displayDate = getFormattedDate();
    const { gridCols, gridRows, gap, padding, rounded, labName, showLogo, studentName } = config;
    
    const cellWidth = 350; 
    const cellHeight = 350;
    const totalWidth = cellWidth * gridCols + gap * (gridCols - 1) + padding * 2;
    const headerHeight = 220;
    const footerHeight = 70;
    const totalHeight = cellHeight * gridRows + gap * (gridRows - 1) + padding * 2 + headerHeight + footerHeight;

    canvas.width = totalWidth;
    canvas.height = totalHeight;
    ctx.fillStyle = '#ffffff';
    ctx.fillRect(0, 0, totalWidth, totalHeight);

    let currentY = padding + 40;
    let logoWidth = 0;
    if (showLogo && logo) {
      const logoImg = new Image();
      logoImg.src = logo;
      await new Promise<void>((resolve) => {
        logoImg.onload = () => resolve();
        logoImg.onerror = () => resolve();
      });
      const logoSize = 80;
      ctx.drawImage(logoImg, padding, currentY, logoSize, logoSize);
      logoWidth = logoSize + 24;
    }

    ctx.fillStyle = '#1e3a8a';
    ctx.font = 'bold 56px Inter';
    ctx.textAlign = 'left';
    ctx.textBaseline = 'middle';
    ctx.fillText(labName, padding + logoWidth, currentY + 30);
    
    ctx.fillStyle = '#64748b';
    ctx.font = '300 28px Inter';
    ctx.fillText(slogan, padding + logoWidth, currentY + 75);

    const cardPadding = 30;
    const blueLineW = 8;
    ctx.font = 'bold 44px Inter';
    const nameMeasure = ctx.measureText(studentName).width;
    const dateMeasure = ctx.measureText(displayDate).width;
    const maxTextWidth = Math.max(nameMeasure, dateMeasure);
    const cardW = maxTextWidth + cardPadding * 3 + blueLineW + 20;
    const cardH = 120;
    const cardX = totalWidth - padding - cardW;
    const cardY = currentY - 10;

    ctx.fillStyle = '#ffffff';
    ctx.beginPath();
    // @ts-ignore
    if (ctx.roundRect) ctx.roundRect(cardX, cardY, cardW, cardH, 20); else ctx.rect(cardX, cardY, cardW, cardH);
    ctx.fill();

    ctx.fillStyle = '#3b82f6';
    ctx.beginPath();
    // @ts-ignore
    if (ctx.roundRect) ctx.roundRect(cardX + cardPadding, cardY + cardPadding, blueLineW, cardH - cardPadding * 2, 4); else ctx.rect(cardX + cardPadding, cardY + cardPadding, blueLineW, cardH - cardPadding * 2);
    ctx.fill();

    ctx.fillStyle = '#1e293b';
    ctx.font = 'bold 44px Inter';
    ctx.fillText(studentName, cardX + cardPadding + blueLineW + 25, cardY + cardPadding + 15);
    ctx.fillStyle = '#94a3b8';
    ctx.font = 'medium 32px Inter';
    ctx.fillText(displayDate, cardX + cardPadding + blueLineW + 25, cardY + cardH - cardPadding + 5);

    currentY += 150;

    for (let r = 0; r < gridRows; r++) {
      for (let c = 0; c < gridCols; c++) {
        const idx = r * gridCols + c;
        const x = padding + c * (cellWidth + gap);
        const y = currentY + r * (cellHeight + gap);
        const imgData = images[idx]?.url;

        if (imgData) {
          const img = new Image();
          img.src = imgData;
          await new Promise<void>((resolve) => {
            img.onload = () => resolve();
            img.onerror = () => resolve();
          });

          ctx.save();
          ctx.beginPath();
          // @ts-ignore
          if (ctx.roundRect) ctx.roundRect(x, y, cellWidth, cellHeight, rounded * 4); else ctx.rect(x, y, cellWidth, cellHeight);
          ctx.clip();

          const imgAspect = img.width / img.height;
          const cellAspect = cellWidth / cellHeight;
          let drawW, drawH, drawX, drawY;

          if (imgAspect > cellAspect) {
            drawH = cellHeight;
            drawW = img.width * (cellHeight / img.height);
            drawX = x - (drawW - cellWidth) / 2;
            drawY = y;
          } else {
            drawW = cellWidth;
            drawH = img.height * (cellWidth / img.width);
            drawX = x;
            drawY = y - (drawH - cellHeight) / 2;
          }
          ctx.drawImage(img, drawX, drawY, drawW, drawH);
          ctx.fillStyle = 'rgba(255, 255, 255, 0.7)';
          ctx.font = 'bold 24px Inter';
          ctx.textAlign = 'right';
          ctx.fillText(watermarkText, x + cellWidth - 15, y + cellHeight - 15);
          ctx.restore();
        } else {
          ctx.fillStyle = '#f8fafc';
          ctx.beginPath();
          // @ts-ignore
          if (ctx.roundRect) ctx.roundRect(x, y, cellWidth, cellHeight, rounded * 4); else ctx.rect(x, y, cellWidth, cellHeight);
          ctx.fill();
        }
      }
    }

    ctx.fillStyle = '#94a3b8';
    ctx.font = 'bold 24px Inter';
    ctx.textAlign = 'right';
    ctx.fillText(`DESIGNED BY HICODE AI LAB`, totalWidth - padding, totalHeight - 35);

    const link = document.createElement('a');
    link.download = `${labName}-${studentName}-${gridSize}宫格.png`;
    link.href = canvas.toDataURL('image/png', 1.0);
    link.click();
    setIsExporting(false);
  };

  return (
    <div className="min-h-screen bg-slate-50 text-slate-900 pb-12 font-sans">
      <header className="bg-white border-b border-slate-200 sticky top-0 z-30 px-6 py-4 shadow-sm">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="bg-blue-600 p-2 rounded-lg text-white"><Zap size={24} /></div>
            <div>
              <div className="flex items-center gap-2">
                <h1 className="text-xl font-bold text-slate-900 leading-none">{config.labName}</h1>
                {saveStatus && <span className="text-[10px] bg-green-100 text-green-600 px-1.5 py-0.5 rounded flex items-center gap-1 animate-pulse"><CheckCircle2 size={10}/>配置已同步</span>}
              </div>
              <p className="text-xs text-slate-500 mt-1 uppercase tracking-wider font-semibold">{gridSize}宫格 AI 处理器</p>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <button onClick={clearImages} className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-slate-600 hover:text-red-600 transition-colors"><RefreshCcw size={16} />清空照片</button>
            <button onClick={exportGrid} disabled={isExporting} className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-5 py-2.5 rounded-full font-semibold shadow-lg shadow-blue-200 transition-all active:scale-95 disabled:opacity-50">
              {isExporting ? <RefreshCcw className="animate-spin" size={20} /> : <Download size={20} />}导出高清图
            </button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 mt-6 grid grid-cols-1 lg:grid-cols-4 gap-6">
        <div className="lg:col-span-1 space-y-6">
          <section className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100">
            <h2 className="flex items-center gap-2 text-sm font-bold text-slate-400 uppercase tracking-widest mb-4"><Upload size={14} /> 批量上传</h2>
            <div onClick={() => fileInputRef.current?.click()} className="border-2 border-dashed border-slate-200 rounded-xl p-6 flex flex-col items-center justify-center gap-3 cursor-pointer hover:border-blue-400 hover:bg-blue-50/50 transition-all group">
              <div className="w-12 h-12 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center group-hover:scale-110 transition-transform"><Camera size={24} /></div>
              <p className="text-sm font-medium text-center text-slate-600">点击上传照片</p>
              <input type="file" multiple accept="image/*" className="hidden" ref={fileInputRef} onChange={handleImageUpload}/>
            </div>
          </section>

          <section className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100">
            <h2 className="flex items-center gap-2 text-sm font-bold text-slate-400 uppercase tracking-widest mb-4"><LayoutGrid size={14} /> 宫格排版</h2>
            <div className="grid grid-cols-2 gap-2">
              {[{ label: '9 宫格', cols: 3, rows: 3 }, { label: '16 宫格', cols: 4, rows: 4 }, { label: '18 宫格', cols: 6, rows: 3 }, { label: '24 宫格', cols: 6, rows: 4 }, { label: '25 宫格', cols: 5, rows: 5 }, { label: '36 宫格', cols: 6, rows: 6 }].map((layout) => (
                <button key={layout.label} onClick={() => switchLayout(layout.cols, layout.rows)} className={`px-3 py-2 rounded-lg text-xs font-bold border transition-all ${config.gridCols === layout.cols && config.gridRows === layout.rows ? 'bg-blue-600 border-blue-600 text-white shadow-md' : 'bg-slate-50 border-slate-200 text-slate-600 hover:border-blue-300'}`}>
                  {layout.label}
                </button>
              ))}
            </div>
          </section>

          <section className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100">
            <h2 className="flex items-center gap-2 text-sm font-bold text-slate-400 uppercase tracking-widest mb-4"><Settings size={14} /> 实验室配置</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-xs font-bold text-slate-500 mb-2 uppercase flex items-center justify-between">实验室 Logo {LOCKED_LOGO_BASE64 && <Lock size={10}/>}</label>
                <div className="flex items-center gap-3">
                  <div onClick={() => !LOCKED_LOGO_BASE64 && logoInputRef.current?.click()} className={`w-12 h-12 rounded-lg border flex items-center justify-center overflow-hidden relative group ${LOCKED_LOGO_BASE64 ? 'bg-blue-50 cursor-not-allowed' : 'bg-slate-100 cursor-pointer'}`}>
                    {logo ? <img src={logo} alt="Logo" className="w-full h-full object-cover" /> : <Upload size={16} className="text-slate-400" />}
                  </div>
                  <input type="file" className="hidden" ref={logoInputRef} onChange={handleLogoUpload} accept="image/*" />
                </div>
              </div>
              <div><label className="block text-xs font-bold text-slate-500 mb-2 uppercase">实验室名称</label><input type="text" value={config.labName} onChange={(e) => setConfig({...config, labName: e.target.value})} className="w-full bg-slate-50 border border-slate-200 rounded-lg px-3 py-2 text-sm focus:ring-2 focus:ring-blue-500 outline-none" /></div>
              <div><label className="block text-xs font-bold text-slate-500 mb-2 uppercase">学生姓名</label><div className="relative"><input type="text" value={config.studentName} onChange={(e) => setConfig({...config, studentName: e.target.value})} className="w-full bg-slate-50 border border-slate-200 rounded-lg pl-9 pr-3 py-2 text-sm focus:ring-2 focus:ring-blue-500 outline-none" /><User size={14} className="absolute left-3 top-3 text-slate-400" /></div></div>
              <div><label className="block text-xs font-bold text-slate-500 mb-2 uppercase">选择日期</label><input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} className="w-full bg-slate-50 border border-slate-200 rounded-lg px-3 py-2 text-sm focus:ring-2 focus:ring-blue-500 outline-none" /></div>
            </div>
          </section>
        </div>

        <div className="lg:col-span-3">
          <div className="bg-white p-4 sm:p-8 rounded-3xl shadow-xl border border-slate-100 min-h-[600px] flex flex-col">
            <div className="mb-4 flex items-center justify-center min-h-[32px]">
              {swapSourceIdx !== null && (
                <div className="bg-blue-50 text-blue-600 text-xs font-bold px-4 py-2 rounded-full flex items-center gap-2 animate-bounce border border-blue-100"><ArrowLeftRight size={14} /> 请点击另一个格子进行调换</div>
              )}
            </div>
            
            <div className="mb-8 flex flex-col sm:flex-row sm:items-start justify-between gap-4">
              <div className="flex items-center gap-4">
                {logo ? <div className="p-2 bg-slate-50 rounded-xl border border-slate-100"><img src={logo} alt="Logo" className="h-12 w-auto" /></div> : <div className="w-12 h-12 bg-slate-100 rounded-xl flex items-center justify-center text-slate-300"><Zap size={24} /></div>}
                <div><h2 className="text-xl sm:text-2xl font-black text-slate-900 tracking-tight leading-none">{config.labName}</h2><p className="text-slate-500 text-xs sm:text-sm font-medium mt-1">{slogan}</p></div>
              </div>
              <div className="bg-white border border-slate-100 rounded-2xl p-3 flex items-center gap-3 shadow-sm">
                <div className="w-1.5 h-10 bg-blue-500 rounded-full" /><div className="flex flex-col"><span className="text-lg font-bold text-slate-800 leading-none mb-1">{config.studentName}</span><span className="text-xs font-medium text-slate-400 font-mono">{getFormattedDate()}</span></div>
              </div>
            </div>

            <div className="grid mx-auto w-full max-w-4xl" style={{ display: 'grid', gridTemplateColumns: `repeat(${config.gridCols}, 1fr)`, gap: `${config.gap}px` }}>
              {currentImages.map((img, idx) => (
                <div key={img.id} onClick={() => handleGridClick(idx)} className={`aspect-square relative overflow-hidden border transition-all cursor-pointer group ${swapSourceIdx === idx ? 'ring-4 ring-blue-500 scale-[1.02] z-10' : 'border-slate-100 hover:border-blue-300'}`} style={{ borderRadius: `${config.rounded}px` }}>
                  {img.url ? (
                    <>
                      <img src={img.url} alt={`Grid ${idx}`} className="w-full h-full object-cover" />
                      <div className="absolute bottom-1 right-1 px-1 py-0.5 bg-black/10 backdrop-blur-sm rounded text-[8px] font-bold text-white/70">{watermarkText}</div>
                      <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">{swapSourceIdx === null && <button onClick={(e) => removeImage(idx, e)} className="bg-red-500 text-white p-2 rounded-full"><Trash2 size={16} /></button>}</div>
                    </>
                  ) : (
                    <div className="w-full h-full flex items-center justify-center bg-slate-50 text-slate-200"><span className="text-[10px] font-black">{idx + 1}</span></div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>
      <canvas ref={canvasRef} className="hidden" />
    </div>
  );
};

export default App;